import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Button = ({handleClick, text}) => (
  <button onClick={handleClick}>
    {text}
  </button>
)

const Display = ({rating, counter}) => <div>{rating} {counter}</div>
const Percentage = ({rating, counter, total}) => <div>{rating} {(counter/total)*100} %</div>

const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const handleGoodClick = () => {
    setGood(good + 1)
  }
  const handleNeutralClick = () => {
    setNeutral(neutral + 1)
  }
  const handleBadClick = () => {
    setBad(bad + 1)
  }
  return (
    <div>
      <h3>Give Feedback</h3>
      <Button handleClick={handleGoodClick} text='good'/>
      <Button handleClick={handleNeutralClick} text='neutral'/>
      <Button handleClick={handleBadClick} text='good'/>
      <h3>Statistics</h3>
      <Display rating='good' counter={good}/>
      <Display rating='neutral' counter={neutral}/>
      <Display rating='bad' counter={bad}/>
      <Display rating='all' counter={good+neutral+bad}/>
      <Percentage rating='positive' counter={good} total={good+neutral+bad}/>
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
